import {
  ChangeDetectionStrategy,
  Component,
  Input,
  OnChanges,
  OnInit,
} from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'page-error',
  templateUrl: './page-error.component.html',
  styleUrls: ['./page-error.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class PageErrorComponent implements OnInit, OnChanges {
  constructor(private route: ActivatedRoute) {}

  @Input() code = 0;

  translated = 'default';

  ngOnInit() {
    this.code ||= this.route.snapshot.data['code'];
    this.translated = [500, 404, 403, 401, 400].includes(this.code)
      ? this.code.toString()
      : 'default';
  }

  ngOnChanges() {
    this.ngOnInit();
  }
}
<section>
  <div class="img">
    <i icon="error"></i>
  </div>
  <h2>{{ 'errors.error' | translate }} {{ code || '' }}</h2>
  <p>{{ 'errors.' + translated | translate }}</p>
  <div class="buttons center">
    <a mat-button class="primary" routerLink="/" *ngIf="code !== 401">
      {{ 'navbar.home' | translate }}
    </a>
    <a mat-button routerLink="/login" *ngIf="code === 401">
      {{ 'navbar.login' | translate }}
    </a>
    <a mat-button routerLink="/help">Ajuda</a>
  </div>
</section>
