import { Component, OnInit } from '@angular/core';
import { MatTableDataSource } from '@angular/material/table';

@Component({
  selector: 'app-tabela',
  templateUrl: './tabela.component.html',
  styleUrls: ['./tabela.component.scss']
})
export class TabelaComponent implements OnInit {
  dataSource: MatTableDataSource<any> = new MatTableDataSource<any>([]);
  displayedColumns: string[] = ['guidingQuestions', 'guidingActivities', 'guidingResources', 'resultados'];

  ngOnInit() {
    this.dataSource.data = [
      { guidingQuestions: ' ', guidingActivities: ' ', guidingResources: ' ', resultados: ' ' }
    ];
  }

  showFilter: boolean = false;
  isVisible: boolean = true;

  toggleFilter() {
    this.showFilter = !this.showFilter;
  }

  adicionarLinha(linha?: any) {
    const index = linha ? this.dataSource.data.indexOf(linha) : this.dataSource.data.length - 1;
    const novaLinha = { guidingQuestions: '', guidingActivities: '', guidingResources: '', resultados: '' };
    this.dataSource.data.splice(index + 1, 0, novaLinha);
    this.dataSource._updateChangeSubscription();
  }

  removerLinha(linha: any) {
    const confirmacao = confirm("Tem certeza que deseja deletar esta linha?");
    if (confirmacao) {
      const index = this.dataSource.data.indexOf(linha);
      if (index >= 0) {
        this.dataSource.data.splice(index, 1);
        this.dataSource._updateChangeSubscription();
        this.verificarTabelaVazia();
      }
    }
  }

  verificarTabelaVazia() {
    if (this.dataSource.data.length === 0) {
      this.dataSource.data = [];
      this.showFilter = false;
    }
  }

  autoResize(event: any) {
    event.target.style.height = 'auto';
    event.target.style.height = (event.target.scrollHeight) + 'px';
  }

  applyFilter(filterValue: string) {
    filterValue = filterValue.trim().toLowerCase();

    this.dataSource.filterPredicate = (data: any, filter: string) => {
      const columnsToFilter = Object.keys(data);
      return columnsToFilter.some(column => {
        const value = data[column];
        return value && value.toString().toLowerCase().includes(filter);
      });
    };

    this.dataSource.filter = filterValue;
  }

  removeCanvas() {
    const confirmacao = confirm("Tem certeza que deseja deletar esta tabela? Todos os dados serão perdidos");
    if (confirmacao) {
      this.isVisible = false; 
    }
  }
}
<div *ngIf="dataSource.data.length > 0 && isVisible" class="table-container">
  <button class="delete-button" (click)="removeCanvas()">×</button>
  <div class="button-group filter-toggle-container">
    <button class="filter-toggle-button" mat-raised-button (click)="toggleFilter()">Toggle Filter</button>
  </div>

  <mat-form-field class="filter-field" *ngIf="showFilter">
    <mat-label>Filtro</mat-label>
    <input matInput (keyup)="applyFilter(input.value)" placeholder="Ex. educação" #input>
  </mat-form-field>

  <table mat-table [dataSource]="dataSource" class="mat-elevation-z8">
    <ng-container matColumnDef="guidingQuestions">
      <th mat-header-cell *matHeaderCellDef>
        <div class="header-title">Guiding Questions</div>
      </th>
      <td mat-cell *matCellDef="let linha" class="cell-container">
        <button class="add-button" (click)="adicionarLinha(linha)">+</button>
        <button class="remove-row-button" (click)="removerLinha(linha)">-</button>
        <textarea matInput [(ngModel)]="linha.guidingQuestions" class="tablecell" (input)="autoResize($event)"></textarea>
      </td>
    </ng-container>

    <ng-container matColumnDef="guidingActivities">
      <th mat-header-cell *matHeaderCellDef>
        <div class="header-title">Guiding Activities</div>
      </th>
      <td mat-cell *matCellDef="let linha" class="cell-container">
        <textarea matInput [(ngModel)]="linha.guidingActivities" class="tablecell" (input)="autoResize($event)"></textarea>
      </td>
    </ng-container>

    <ng-container matColumnDef="guidingResources">
      <th mat-header-cell *matHeaderCellDef>
        <div class="header-title">Guiding Resources</div>
      </th>
      <td mat-cell *matCellDef="let linha" class="cell-container">
        <textarea matInput [(ngModel)]="linha.guidingResources" class="tablecell" (input)="autoResize($event)"></textarea>
      </td>
    </ng-container>

    <ng-container matColumnDef="resultados">
      <th mat-header-cell *matHeaderCellDef>
        <div class="header-title">Resultados</div>
      </th>
      <td mat-cell *matCellDef="let linha" class="cell-container">
        <textarea matInput [(ngModel)]="linha.resultados" class="tablecell" (input)="autoResize($event)"></textarea>
      </td>
    </ng-container>

    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let linha; columns: displayedColumns;"></tr>
  </table>
</div>
