import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { MatDialog } from '@angular/material/dialog';
import { Router } from '@angular/router';
import { zoomInAnimation } from '@animations/route-animation';
import { ForgotPasswordComponent } from '@app/components/modals/forgot-password/forgot-password.component';
import { CookiesLoginComponent } from '@components/modals/cookies-login/cookies-login.component';
import { environment } from '@env';
import { AuthService } from '@services/auth.service';
import { BodyJson } from '@services/http.service';
import { StorageService } from '@services/storage.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
  animations: [zoomInAnimation],
})
export class LoginComponent implements OnInit {
  constructor(
    private fb: FormBuilder,
    public dialog: MatDialog,
    public storage: StorageService,
    private authService: AuthService,
    public router: Router
  ) {}

  loading = false;
  view_pass = false;

  version = environment.version;

  login_form = this.fb.group({
    email: ['', [Validators.required, Validators.email]],
    password: ['', Validators.required],
    remember: [false],
  });

  ngOnInit(): void {
    this.awaitRemember();
  }

  loginSubmitHandler() {
    if (this.login_form.invalid) {
      this.login_form.markAllAsTouched();
      return;
    }
  
    this.loading = true;
  
    // Extraindo os valores de email, password e remember do formulário
    const email = this.login_form.get('email')?.value ?? '';  // Garante que email nunca seja null ou undefined
    const password = this.login_form.get('password')?.value ?? '';  // Garante que password nunca seja null ou undefined
    const remember = this.login_form.get('remember')?.value;
    // Passando email e password como argumentos separados para o login
    this.authService.login(email, password).subscribe({
      next: (response) => {
        this.loading = false;
        this.authService.setToken(response.token, remember as boolean);  // `remember` já foi extraído
        this.router.navigate(['/']);
      },
      error: () => {
        this.loading = false;
      },
    });
  }
  

  awaitRemember() {
    this.login_form.get('remember')?.valueChanges.subscribe((value) => {
      if (!value) return;

      if (!this.storage.cookies) {
        this.openCookieDialog();
      }
    });
  }

  handleOpenForgotPasswordModal() {
    this.dialog.open(ForgotPasswordComponent, {
      data: { email: this.login_form.get('email')?.value },
    });
  }

  openCookieDialog() {
    const dialogRef = this.dialog.open(CookiesLoginComponent, {
      panelClass: 'cookies-dialog',
      disableClose: true,
    });

    dialogRef.afterClosed().subscribe((result: boolean) => {
      if (result) {
        this.storage.cookies = true;
      } else {
        this.login_form.get('remember')?.setValue(false);
      }
    });
  }
}
<main id="login">
  <div [@zoomInAnimation] class="row">
    <div class="lang">
      <lang-select></lang-select>
    </div>
    <div class="col">
      <div class="logo">
        <img src="assets/images/logocblxtool.png" alt="Logo" />
      </div>
    </div>
    <div class="col">
      <div class="form">
        <form (ngSubmit)="loginSubmitHandler()" [formGroup]="login_form">
          <h1>{{ 'login-register.login-title' | translate }}</h1>
          <p>
            {{ 'login-register.not-registered' | translate }}
            <a routerLink="/register">{{
              'login-register.go-register' | translate
            }}</a>
          </p>

          <mat-form-field appearance="outline">
            <mat-label>
              {{ 'login-register.email' | translate }}
            </mat-label>
            <input
              matInput
              type="email"
              formControlName="email"
              autocomplete="username"
            />
            <mat-error>
              {{ login_form.controls.email.errors | formError | translate }}
            </mat-error>
          </mat-form-field>
          <mat-form-field appearance="outline">
            <mat-label>
              {{ 'login-register.password' | translate }}
            </mat-label>
            <input
              matInput
              [type]="view_pass ? 'text' : 'password'"
              formControlName="password"
              autocomplete="current-password"
            />

            <button
              type="button"
              mat-icon-button
              matSuffix
              aria-label="Ver senha"
              (click)="view_pass = !view_pass"
            >
              <i icon="view_pass" *ngIf="!view_pass"></i>
              <i icon="hide_pass" *ngIf="view_pass"></i>
            </button>

            <mat-error>
              {{ login_form.controls.password.errors | formError | translate }}
            </mat-error>
          </mat-form-field>

          <div class="forgot-password">
            <a class="f-12" (click)="handleOpenForgotPasswordModal()">
              {{ 'login-register.forgot-password' | translate }}
            </a>
          </div>

          <mat-checkbox formControlName="remember">
            {{ 'login-register.remember-me' | translate }}
          </mat-checkbox>

          <div class="buttons full">
            <button
              mat-button
              type="submit"
              class="primary"
              [disabled]="loading"
            >
              {{ 'login-register.login-title' | translate }}
              <loading *ngIf="loading"></loading>
            </button>
          </div>
        </form>
      </div>
    </div>

    <div class="version">v{{ version }}</div>
  </div>
</main>
