import { Component, OnInit } from '@angular/core';
import {
  AbstractControl,
  FormBuilder,
  FormControl,
  Validators,
} from '@angular/forms';
import { MatDialog } from '@angular/material/dialog';
import { Router } from '@angular/router';
import { zoomInAnimation } from '@animations/route-animation';
import { Genre } from '@models/user';
import { AuthService } from '@services/auth.service';
import { BodyJson } from '@services/http.service';
import { StorageService } from '@services/storage.service';
import { cpfValidator } from '@utils/validators';
import { Score, zxcvbn, zxcvbnOptions } from '@zxcvbn-ts/core';
import * as zxcvbnCommonPackage from '@zxcvbn-ts/language-common';
import * as zxcvbnBrPackage from '@zxcvbn-ts/language-pt-br';
import { NotifierService } from 'angular-notifier';
import { RegisterService } from '@services/register.service';  // Ajuste o caminho conforme necessário


@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss'],
  animations: [zoomInAnimation],
})
export class RegisterComponent implements OnInit {
  constructor(
    private fb: FormBuilder,
    public dialog: MatDialog,
    private registerService: RegisterService,
    public storage: StorageService,
    private authService: AuthService,
    public router: Router,
    private notifier: NotifierService
  ) {}

  loading = false;
  view_pass = false;
  view_repass = false;
  score: Score = 0;
  now = new Date();
  password_warning = '';
  password_suggestions: string[] = [];

  register_form = this.fb.group({
    name: ['', [Validators.required]],
    email: ['', [Validators.required, Validators.email]],
    phone: ['', [Validators.required]],
    birth_date: [this.now, [Validators.required]],
    // cpf: ['', [Validators.required, cpfValidator]],
    // genre: [new FormControl<Genre>('M'), [Validators.required]],
    password: ['', [Validators.required, this.validScore()]],
    re_password: ['', [Validators.required, this.samePassword()]],
  });

  ngOnInit(): void {
    this.register_form.get('birth_date')?.reset();

    this.register_form.get('password')?.valueChanges.subscribe((value) => {
      this.strengthPassword(value || '');
    });
  }


  loginSubmitHandler() {
    if (this.register_form.invalid) {
      this.register_form.markAllAsTouched();
      return;
    }
  
    this.loading = true;
  
    // Utilize o RegisterService em vez do AuthService
    this.registerService.register_user(this.register_form.value as BodyJson).subscribe({
      next: () => {
        this.loading = false;
        this.router.navigate(['/login']);
        this.notifier.notify('success', 'Registrado com sucesso!');
      },
      error: (errorResponse) => {
        this.loading = false;
        console.error('Erro de registro (resposta completa):', errorResponse); // Depuração completa
        console.error('Erro de registro (mensagem de erro):', errorResponse.error); // Depuração da mensagem de erro específica
  
        // Agora verificamos se o erro contém a chave "error" que possui a mensagem "Este email já está em uso."
        if (errorResponse.error && errorResponse.error.error && errorResponse.error.error.includes('email já está em uso')) {
          this.notifier.notify('error', 'Este email já está em uso. Tente outro.');
        } else {
          this.notifier.notify('error', 'Ocorreu um erro no registro. Tente novamente.');
        }
      },
    });
  }
  
  
  samePassword() {
    return (control: AbstractControl) => {
      const password = control.value;
      if (password) {
        if (password !== this.register_form.value.password) {
          return { diff_password: true };
        }
      }
      return null;
    };
  }

  validScore() {
    return (control: AbstractControl) => {
      const password = control.value;
      if (password) {
        if (this?.score < 3) {
          return { poor_password: true };
        }
      }
      return null;
    };
  }

  get password() {
    return this.register_form.get('password')?.value as string;
  }

  strengthPassword(password: string) {
    const options = {
      translations: zxcvbnBrPackage.translations,
      graphs: zxcvbnCommonPackage.adjacencyGraphs,
      dictionary: {
        ...zxcvbnCommonPackage.dictionary,
        ...zxcvbnBrPackage.dictionary,
      },
    };
    zxcvbnOptions.setOptions(options);
    const passwordStatus = zxcvbn(password);
    this.score = passwordStatus.score;
    this.password_warning = passwordStatus.feedback.warning || '';
    this.password_suggestions = passwordStatus.feedback.suggestions;
  }
}
<main id="register">
  <div [@zoomInAnimation] class="row">
    <div class="lang">
      <lang-select></lang-select>
    </div>
    <div class="col">
      <div class="logo">
        <img src="assets/images/logocblxtool.png" alt="Logo" />
      </div>
    </div>
    <div class="col">
      <div class="form">
        <form (ngSubmit)="loginSubmitHandler()" [formGroup]="register_form">
          <h1>{{ 'login-register.register-title' | translate }}</h1>
          <p>
            {{ 'login-register.already-registered' | translate }}
            <a routerLink="/login">
              {{ 'login-register.go-login' | translate }}
            </a>
          </p>

          <mat-form-field appearance="outline">
            <mat-label>
              {{ 'login-register.name' | translate }}
            </mat-label>
            <input matInput type="text" formControlName="name" />
            <mat-error>
              {{ register_form.controls.name.errors | formError | translate }}
            </mat-error>
          </mat-form-field>

          <mat-form-field appearance="outline">
            <mat-label>
              {{ 'login-register.email' | translate }}
            </mat-label>
            <input
              matInput
              type="email"
              autocomplete="username"
              formControlName="email"
            />

            <mat-error>
              {{ register_form.controls.email.errors | formError | translate }}
            </mat-error>
          </mat-form-field>

          <mat-form-field appearance="outline">
            <mat-label>
              {{ 'login-register.cellphone' | translate }}
            </mat-label>
            <input
              matInput
              type="text"
              mask="(00) 00000-0000"
              formControlName="phone"
            />
            <mat-error>
              {{ register_form.controls.phone.errors | formError | translate }}
            </mat-error>
          </mat-form-field>

          <!-- <mat-form-field appearance="outline">
            <mat-label>
              {{ 'login-register.cpf' | translate }}
            </mat-label>
            <input
              matInput
              type="text"
              mask="000.000.000-00"
              formControlName="cpf"
            />
            <mat-error>
              {{ register_form.controls.cpf.errors | formError | translate }}
            </mat-error>
          </mat-form-field> -->

          <mat-form-field appearance="outline">
            <mat-label>
              {{ 'login-register.birth-date' | translate }}
            </mat-label>
            <input
              matInput
              type="text"
              [matDatepicker]="picker"
              formControlName="birth_date"
              [max]="now"
            />
            <mat-datepicker-toggle
              matSuffix
              [for]="picker"
            ></mat-datepicker-toggle>
            <mat-datepicker matSuffix #picker></mat-datepicker>

            <mat-error>
              {{
                register_form.controls.birth_date.errors | formError | translate
              }}
            </mat-error>
          </mat-form-field>

          <!-- <mat-form-field appearance="outline">
            <mat-label>
              {{ 'login-register.genre' | translate }}
            </mat-label>
            <mat-select matInput formControlName="genre">
              <mat-option value="M">
                {{ 'login-register.genres.male' | translate }}
              </mat-option>
              <mat-option value="F">
                {{ 'login-register.genres.famale' | translate }}
              </mat-option>
              <mat-option value="N">
                {{ 'login-register.genres.do-not-inform' | translate }}
              </mat-option>
            </mat-select>

            <mat-error>
              {{ register_form.controls.genre.errors | formError | translate }}
            </mat-error>
          </mat-form-field> -->

          <mat-form-field appearance="outline">
            <mat-label>
              {{ 'login-register.password' | translate }}
            </mat-label>
            <input
              matInput
              [type]="view_pass ? 'text' : 'password'"
              autocomplete="new-password"
              formControlName="password"
            />
            <button
              type="button"
              mat-icon-button
              matSuffix
              aria-label="Ver senha"
              (click)="view_pass = !view_pass"
            >
              <i icon="view_pass" *ngIf="!view_pass"></i>
              <i icon="hide_pass" *ngIf="view_pass"></i>
            </button>

            <mat-error [matTooltip]="this.password_warning">
              {{
                this.password_warning ||
                  this.password_suggestions[0] ||
                  (register_form.controls.password.errors
                    | formError
                    | translate)
              }}
            </mat-error>
          </mat-form-field>

          <mat-form-field appearance="outline">
            <mat-label>
              {{ 'login-register.re-password' | translate }}
            </mat-label>
            <input
              matInput
              [type]="view_repass ? 'text' : 'password'"
              autocomplete="new-password"
              formControlName="re_password"
            />
            <button
              type="button"
              mat-icon-button
              matSuffix
              aria-label="Ver senha"
              (click)="view_repass = !view_repass"
            >
              <i icon="view_pass" *ngIf="!view_repass"></i>
              <i icon="hide_pass" *ngIf="view_repass"></i>
            </button>

            <mat-error>
              {{
                register_form.controls.re_password.errors
                  | formError
                  | translate
              }}
            </mat-error>
          </mat-form-field>

          <div class="buttons full">
            <button
              mat-button
              type="submit"
              class="primary"
              [disabled]="loading"
            >
              {{ 'login-register.register-title' | translate }}
              <loading *ngIf="loading"></loading>
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>
</main>
