import { Component, OnInit } from '@angular/core';
import { AbstractControl, FormBuilder, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { zoomInAnimation } from '@animations/route-animation';
import { AuthService } from '@app/services/auth.service';
import { zxcvbn, zxcvbnOptions } from '@zxcvbn-ts/core';
import * as zxcvbnCommonPackage from '@zxcvbn-ts/language-common';
import * as zxcvbnBrPackage from '@zxcvbn-ts/language-pt-br';
import { NotifierService } from 'angular-notifier';

@Component({
  selector: 'app-rescure-password',
  templateUrl: './rescure-password.component.html',
  styleUrls: ['./rescure-password.component.scss'],
  animations: [zoomInAnimation],
})
export class RescurePasswordComponent implements OnInit {
  constructor(
    private fb: FormBuilder,
    private route: ActivatedRoute,
    private router: Router,
    private authService: AuthService,
    private notifier: NotifierService
  ) {}

  email = '';
  hash = '';

  loading = false;
  view_pass = false;
  view_repass = false;

  form = this.fb.group({
    password: ['', [Validators.required, this.validScore()]],
    re_password: ['', [Validators.required, this.samePassword()]],
  });

  score = 0;
  password_warning = '';
  password_suggestions: string[] = [];

  ngOnInit(): void {
    this.route.queryParams.subscribe((params) => {
      this.email = params['email'];
      this.hash = params['hash'];

      if (!this.email || !this.hash) {
        this.router.navigate(['/']);
      }
    });

    this.form.get('password')?.valueChanges.subscribe((value) => {
      this.strengthPassword(value || '');
    });
  }

  handleFormSubmit() {
    if (this.form.invalid) {
      this.form.markAllAsTouched();
      return;
    }

    this.loading = true;

    const body = {
      email: this.email,
      forgot_password_hash: this.hash,
      new_password: this.form.value.password,
    };

    this.authService.rescurePassword(body).subscribe({
      next: () => {
        this.loading = false;
        this.notifier.notify('success', 'Senha alterada com sucesso!');
        this.router.navigate(['/login']);
      },
      error: () => {
        this.loading = false;
      },
    });
  }

  samePassword() {
    return (control: AbstractControl) => {
      const password = control.value;
      if (password) {
        if (password !== this.form.value.password) {
          return { diff_password: true };
        }
      }
      return null;
    };
  }

  validScore() {
    return (control: AbstractControl) => {
      const password = control.value;
      if (password) {
        if (this?.score < 3) {
          return { poor_password: true };
        }
      }
      return null;
    };
  }

  strengthPassword(password: string) {
    const options = {
      translations: zxcvbnBrPackage.translations,
      graphs: zxcvbnCommonPackage.adjacencyGraphs,
      dictionary: {
        ...zxcvbnCommonPackage.dictionary,
        ...zxcvbnBrPackage.dictionary,
      },
    };
    zxcvbnOptions.setOptions(options);
    const passwordStatus = zxcvbn(password);
    this.score = passwordStatus.score;
    this.password_warning = passwordStatus.feedback.warning || '';
    this.password_suggestions = passwordStatus.feedback.suggestions;
  }
}
<main id="login">
  <div [@zoomInAnimation] class="row">
    <div class="lang">
      <lang-select></lang-select>
    </div>
    <div class="col">
      <div class="logo">
        <img src="assets/images/logocblxtool.png" alt="Logo" />
      </div>
    </div>
    <div class="col">
      <div class="form">
        <form (ngSubmit)="handleFormSubmit()" [formGroup]="form">
          <h1>{{ 'forgot-password.title' | translate }}</h1>
          <p>
            <a routerLink="/login">{{
              'login-register.login-title' | translate
            }}</a>
          </p>

          <mat-form-field appearance="outline">
            <mat-label>
              {{ 'login-register.password' | translate }}
            </mat-label>
            <input
              matInput
              [type]="view_pass ? 'text' : 'password'"
              autocomplete="new-password"
              formControlName="password"
            />
            <button
              type="button"
              mat-icon-button
              matSuffix
              aria-label="Ver senha"
              (click)="view_pass = !view_pass"
            >
              <i icon="view_pass" *ngIf="!view_pass"></i>
              <i icon="hide_pass" *ngIf="view_pass"></i>
            </button>

            <mat-error [matTooltip]="this.password_warning">
              {{
                this.password_warning ||
                  this.password_suggestions[0] ||
                  (form.controls.password.errors | formError | translate)
              }}
            </mat-error>
          </mat-form-field>

          <mat-form-field appearance="outline">
            <mat-label>
              {{ 'login-register.re-password' | translate }}
            </mat-label>
            <input
              matInput
              [type]="view_repass ? 'text' : 'password'"
              autocomplete="new-password"
              formControlName="re_password"
            />
            <button
              type="button"
              mat-icon-button
              matSuffix
              aria-label="Ver senha"
              (click)="view_repass = !view_repass"
            >
              <i icon="view_pass" *ngIf="!view_repass"></i>
              <i icon="hide_pass" *ngIf="view_repass"></i>
            </button>

            <mat-error>
              {{ form.controls.re_password.errors | formError | translate }}
            </mat-error>
          </mat-form-field>

          <div class="buttons full">
            <button
              mat-button
              type="submit"
              class="primary"
              [disabled]="loading"
            >
              {{ 'forgot-password.change-password' | translate }}
              <loading *ngIf="loading"></loading>
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>
</main>
