import { Component, Input, OnInit } from '@angular/core';
import { GRADIENTS } from '@constants/gradients';

@Component({
  selector: 'avatar',
  templateUrl: './avatar.component.html',
  styleUrls: ['./avatar.component.scss'],
})
export class AvatarComponent implements OnInit {
  @Input() name = 'Foo Bar';
  @Input() src = '';
  @Input() length = 2;
  @Input() bg = '';
  @Input() color: 'dark' | 'light' | '' = '';

  gradients = GRADIENTS;
  gradient = this.gradients[0];

  ngOnInit(): void {
    this.gradient =
      this.gradients[Math.floor(Math.random() * this.gradients.length)];

    this.color = this.color || this.gradient.color;
    this.bg = this.bg || this.gradient.bg;
  }
}
<div class="avatar" [ngClass]="{ dark: color === 'dark' }">
  <img alt="{{ name }}" *ngIf="src" [src]="src" [alt]="name" />
  <span
    *ngIf="!src"
    [initialLetters]="name"
    [length]="length"
    [style]="'background: ' + bg"
  ></span>
</div>
