import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA } from '@angular/material/dialog';

export interface IConfirmModalData {
  title: string;
  message: string;
}

@Component({
  selector: 'app-confirm-modal',
  templateUrl: './confirm-modal.component.html',
  styleUrls: ['./confirm-modal.component.scss'],
})
export class ConfirmModalComponent {
  constructor(@Inject(MAT_DIALOG_DATA) public data: IConfirmModalData) {}
}
<h1 matDialogTitle [innerHTML]="data.title"></h1>

<mat-dialog-content [innerHTML]="data.message"> </mat-dialog-content>

<mat-dialog-actions [attr.align]="'center'">
  <button type="button" class="alt" mat-button [mat-dialog-close]="false">
    Cancelar
  </button>
  <button type="button" class="primary" mat-button [mat-dialog-close]="true">
    Confirmar
  </button>
</mat-dialog-actions>
