import {
  Component,
  ElementRef,
  HostListener,
  Input,
  OnInit,
  ViewChild,
} from '@angular/core';
import { takeUntilDestroyed } from '@angular/core/rxjs-interop';
import {
  offcanvasTopAnimation,
  slideInAnimation,
} from '@animations/route-animation';
import { AuthService } from '@services/auth.service';
import { StorageService } from '@services/storage.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss'],
  animations: [slideInAnimation, offcanvasTopAnimation],
})
export class NavbarComponent implements OnInit {
  @Input() colapse: 'vertical' | 'horizontal' = 'vertical';
  @ViewChild('navbar') navbar: ElementRef<HTMLElement> | undefined;

  constructor(
    private storage: StorageService,
    private authService: AuthService
  ) {}

  loading = false;
  error = 0;

  user$ = this.storage.watchUser().pipe(takeUntilDestroyed());

  navbar_hidden = true;
  scroll = false;
  navbar_height = 0;

  ngOnInit(): void {
    this.onWindowScroll();
    this.loading = true;
    this.getMe();

    this.user$.subscribe({
      next: () => {
        this.getMe();
      },
    });
  }

  getMe() {
    this.error = 0;
    this.authService.getMe().subscribe({
      next: () => {
        this.loading = false;
      },
      error: () => {
        // ! ⬆ Adicione o parametro error aqui para que o erro seja capturado
        // this.error = error.status; // ! Descomente esta linha para que o erro seja exibido
        this.loading = false;
      },
    });
  }

  logout() {
    this.authService.logout();
  }

  @HostListener('window:scroll', ['$event'])
  onWindowScroll() {
    const scroll = window.scrollY;
    this.scroll = scroll > 200;

    setTimeout(() => {
      if (this.navbar) {
        this.navbar_height = this.navbar.nativeElement.offsetHeight;
      }
    }, 300);
  }
}
<nav
  [@offcanvasTopAnimation]
  #navbar
  [ngClass]="{ scroll }"
  [ngStyle]="{ '--navbar-height': navbar_height + 'px' }"
>
  <div class="content">
    <a class="logo" routerLink="/">
      <img src="../../../assets/images/mini-logo.png" alt="logo" />
      CBLXTool
    </a>

    <div class="hamburger" [ngClass]="{ active: !navbar_hidden }">
      <button
        aria-label="menu"
        mat-icon-button
        (click)="navbar_hidden = !navbar_hidden"
      >
        <span class="line"></span>
      </button>
    </div>

    <div class="menu {{ colapse }}" [ngClass]="{ hidden: navbar_hidden }">
      <a mat-button routerLink="/">{{ 'navbar.home' | translate }}</a>
      <a mat-button routerLink="/about">{{ 'navbar.about' | translate }}</a>
      <a mat-button routerLink="/contact">{{ 'navbar.contact' | translate }}</a>
      <lang-select></lang-select>
    </div>
  </div>
</nav>

<div
  [@routeAnimations]="outlet && outlet.activatedRouteData"
  *ngIf="!loading && !error"
>
  <router-outlet #outlet="outlet"></router-outlet>
</div>

<page-error [code]="error" *ngIf="error && !loading"></page-error>
